import React from "react";

const Curriculum = (props) => {
    const { courses } = props;
    for (let i = 0; i < props.length; i++){
        const { topic1, topic2, topic3, topic4, topic5, topic6, topic7 } = props[i].syllabus;
}
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 bg-yellow-100">
          {courses.map((course) => (
        <div>
          <h3 className="text-3xl">{course.title}</h3>
          <img
            className="h-24 w-48 m-auto"
            src={course.image}
            alt={course.title}
          />
          <ul>
                  <li>{topic1}</li>
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Curriculum;
