import "./App.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  NavLink,
} from "react-router-dom";
import Home from "./components/Home/Home";
import About from "./components/About/About";
import Curriculum from "./components/Curriculum/Curriculum";
import Services from "./components/Services/Services";
import Contact from "./components/Contact/Contact";
function App() {
  return (
    <div className="App font-sans">
      <Router>

        {/* Home Navlink */}
        <NavLink exact
          to="/"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Home
        </NavLink>
        {/* About Navlink */}
        <NavLink
          to="/about"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          About
        </NavLink>
        {/* Services Navlink */}
        <NavLink
          to="/services"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Services
        </NavLink>
        {/* Curriculum Navlink */}
        <NavLink
          to="/curriculum"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Curriculum
        </NavLink>
        {/* Curriculum Navlink */}
        <NavLink
          to="/contact"
          className="text-2xl"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Contact
        </NavLink>

        <Switch>
          <Route path="/">
            <Home />
          </Route>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/services">
            <Services />
          </Route>
          <Route path="/curriculum">
            <Curriculum />
          </Route>
          <Route path="/contact">
            <Contact />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
