import React from "react";
import { NavLink } from "react-router-dom";

const Header = () => {
  return (
    <div>
      {/* Logo and Heading */}
      <div className="flex m-auto w-1/2 justify-center my-5">
        <img src="./TBL-logo.png" alt="TBL Logo" className="w-1/12" />
        <h1 className="text-5xl">The Backend Legend</h1>
      </div>
      <nav className="bg">
        {/* Home Navlink */}
        <NavLink
          exact
          to="/"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Home
        </NavLink>
        {/* About Navlink */}
        <NavLink
          to="/about"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          About
        </NavLink>
        {/* Services Navlink */}
        <NavLink
          to="/services"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Services
        </NavLink>
        {/* Curriculum Navlink */}
        <NavLink
          to="/curriculum"
          className="text-2xl mr-8"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Curriculum
        </NavLink>
        {/* Curriculum Navlink */}
        <NavLink
          to="/contact"
          className="text-2xl"
          activeStyle={{
            fontWeight: "bold",
            color: "red",
          }}
        >
          Contact
        </NavLink>
      </nav>
    </div>
  );
};

export default Header;
