import React from "react";

const About = (props) => {
  const { courses } = props;
  return (
    <div className="flex justify-evenly my-10">
      <div>
        {/* Instructors */}
        <h3 className="text-4xl font-bold">Our Instructors</h3>
        { courses.map((course) => (
          <h5 className="text-xl">{course.instructor}</h5>
          ))}
      </div>
      {/* Courses */}
      <div>
        <h3 className="text-4xl font-bold">Our Courses</h3>
        { courses.map((course) => (
            <h5 className="text-xl">{course.title}</h5>
            ))}
      </div>
    </div>
  );
};

export default About;
